import { useState } from 'react';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom';
// form
import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
// @mui
import { Link, Stack, Alert, IconButton, InputAdornment } from '@mui/material';
import { LoadingButton } from '@mui/lab';
// auth
import { useAuthContext } from '../../auth/useAuthContext';
// components
import Iconify from '../../components/iconify';
import FormProvider, { RHFTextField } from '../../components/hook-form';

// ----------------------------------------------------------------------

export default function AuthResetPasswordForm() {
    const navigate = useNavigate();
    const { changePassword, logout } = useAuthContext();

    const [showNewPassword, setShowNewPassword] = useState(false);
    const [showConfirmedNewPassword, setShowConfirmedNewPassword] = useState(false);

    const ResetPasswordSchema = Yup.object().shape({
        newPassword: Yup.string().required('New Password is required')
    });

    const methods = useForm({
        resolver: yupResolver(ResetPasswordSchema),
        // defaultValues: { newPassword: 'demo@minimals.cc' },
    });

    const {
        reset,
        setError,
        handleSubmit,
        formState: { isSubmitting },
    } = methods;

    const onSubmit = async (data) => {
        try {
            await changePassword(data.newPassword);
            logout();
            navigate('/login');
        } catch (error) {
            console.error(error.message);

            reset();

            setError('afterSubmit', {
                ...error,
                message: error.message
            });
        }
    };

    return (
        <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
            <Stack spacing={3}>
                <RHFTextField
                    name="newPassword"
                    label="New Password"
                    type={showNewPassword ? 'text' : 'password'}
                    InputProps={{
                        endAdornment: (
                            <InputAdornment position="end">
                                <IconButton onClick={() => setShowNewPassword(!showNewPassword)} edge="end">
                                    <Iconify icon={showNewPassword ? 'eva:eye-fill' : 'eva:eye-off-fill'} />
                                </IconButton>
                            </InputAdornment>
                        ),
                    }}
                />
                <RHFTextField
                    name="confirmedNewPassword"
                    label="Confirm new password"
                    type={showConfirmedNewPassword ? 'text' : 'password'}
                    InputProps={{
                        endAdornment: (
                            <InputAdornment position="end">
                                <IconButton onClick={() => setShowConfirmedNewPassword(!showConfirmedNewPassword)} edge="end">
                                    <Iconify icon={showConfirmedNewPassword ? 'eva:eye-fill' : 'eva:eye-off-fill'} />
                                </IconButton>
                            </InputAdornment>
                        ),
                    }}
                />
            </Stack>

            <LoadingButton fullWidth size="large" type="submit" variant="contained" loading={isSubmitting} sx={{ mt: 3 }}>
                Change Password
            </LoadingButton>
        </FormProvider>
    );
}
